import React, { Component } from "react";
import axios from "axios";
import { Redirect } from "react-router-dom";
import cookie from "react-cookies";
import Header from "./Header";
import "./App.css";

class Profile extends Component {
    constructor(props) {
        super(props);
        this.state = {
            firstname: "",
            lastname: "",
            about: "",
            gender: "",
            phone: "",
            company: "",
            languages: "",
            location: ""
        };


        this.firstnameChangeHandler = this.firstnameChangeHandler.bind(this);
        this.lastnameChangeHandler = this.lastnameChangeHandler.bind(this);
        this.aboutChangeHandler = this.aboutChangeHandler.bind(this);
        this.genderChangeHandler = this.genderChangeHandler.bind(this);
        this.phoneChangeHandler = this.phoneChangeHandler.bind(this);
        this.companyChangeHandler = this.companyChangeHandler.bind(this);
        this.languagesChangeHandler = this.languagesChangeHandler.bind(this);
        this.locationChangeHandler = this.locationChangeHandler.bind(this);

        this.handleSubmit = this.handleSubmit.bind(this);

    }

    phoneChangeHandler = e => {
        this.setState({
            phone: e.target.value
        });
    };
    aboutChangeHandler = e => {
        this.setState({
            about: e.target.value
        });
    };
    genderChangeHandler = e => {
        this.setState({
            gender: e.target.value
        });
    };


    locationChangeHandler = e => {
        this.setState({
            location: e.target.value
        });
    };
    languagesChangeHandler = e => {
        this.setState({
            languages: e.target.value
        });
    };
    companyChangeHandler = e => {
        this.setState({
            company: e.target.value
        });
    };

    firstnameChangeHandler = e => {
        this.setState({
            firstname: e.target.value
        });
    };

    lastnameChangeHandler = e => {
        this.setState({
            lastname: e.target.value
        });
    };

    componentWillMount() {
        var email=cookie.load("travellercookie");
        console.log(email);
        const data={
            email:email
        }
        console.log(data);
        axios.post("http://localhost:3001/getprofile",data).then(response => {
          //update the state with the response data
            console.log(response);
            this.setState({
            firstname: this.state.firstname.concat(response.data[0].FIRST_NAME),
            lastname:  this.state.lastname.concat(response.data[0].LAST_NAME),
            about:  this.state.about.concat(response.data[0].about),
            gender: this.state.gender.concat(response.data[0].gender),
            phone: this.state.phone.concat(response.data[0].phone),
            company:  this.state.company.concat(response.data[0].company),
            languages: this.state.languages.concat(response.data[0].languages),
            location:  this.state.location.concat(response.data[0].location)
          });
        });
      }

    handleSubmit = e => {
        e.preventDefault();

        const data = {
            firstname: this.state.firstname,
            lastname: this.state.lastname,
            about: this.state.about,
            gender: this.state.gender,
            phone: this.state.phone,
            company: this.state.company,
            languages: this.state.languages,
            location: this.state.location

        };
        //set the with credentials to true
        axios.defaults.withCredentials = true;
        axios.post("http://localhost:3001/TravellerUpdate", data).then(response => {
            console.log("Axios POST response:", response.status);

            if (response.status === 200) {
                alert("signup successfull");
                this.setState({
                    authFlag: true,
                    mainredirectVar: <Redirect to="/home" />

                });

            } else {
                console.log("Signup unsuccessful!");
                this.setState({ authFlag: false });
            }
        });
    };



    render() {
        // let redirectVar = null;

        // if (!(cookie.load("travellercookie") || cookie.load("ownercookie"))) {

        //     redirectVar = <Redirect to="/home" />;
        // }


        return (
            <div>
                {/* {redirectVar} */}
                <Header>
                </Header>
                <div className="profile-container">
                    <div className="row">
                        <div className="col-md-3" >
                            <img className="profile-image" src="data:image/png;base64," class="rounded-circle" alt="Cinque Terre" />

                        </div>
                        <div className="col-md-8">
                            <div className="panel-body">
                                <h4>Profile</h4>
                                <form className="location-form">
                                    <div className="form-group">
                                        <label>First Name</label>
                                        <input id="firstname" type="text" value={this.state.firstname} onChange={this.firstnameChangeHandler} className="form-control" />
                                    </div>
                                    <div className="form-group">
                                        <label>Last Name</label>
                                        <input id="lastname" type="text" value={this.state.lastname} onChange={this.lastnameChangeHandler} className="form-control" />
                                    </div>
                                    <div className="form-group">
                                        <label>About Me</label>
                                        <input id="aboutme" type="text" value={this.state.about} onChange={this.aboutChangeHandler} className="form-control" />
                                    </div>

                                    <div className="form-group">
                                        <label>Gender</label>
                                        <input id="gender" type="text" value={this.state.gender} onChange={this.genderChangeHandler} className="form-control" />
                                    </div>

                                    <div className="form-group">
                                        <label>Phone</label>
                                        <input id="phone" type="text" value={this.state.phone} onChange={this.phoneChangeHandler} className="form-control" />
                                    </div>
                                    <div className="form-group">
                                        <label>Company</label>
                                        <input id="postal" type="text" value={this.state.company} onChange={this.companyChangeHandler} className="form-control" />
                                    </div>
                                    <div className="form-group">
                                        <label>Languages</label>
                                        <input id="language" type="text" value={this.state.language} onChange={this.languageChangeHandler} className="form-control" />
                                    </div>
                                   
                                    <button className="btn btn-primary" onClick={this.handleSubmit}>Save</button>
                                </form>
                            </div>
                        </div>
                        <div className="col-md-1" ></div>
                    </div>
                </div>
            </div>


        )
    }



};

export default Profile;